mapper.select:
SELECT op.*, e.name AS operateEmpName
FROM operate_log op
LEFT JOIN emp e ON e.id = op.operate_emp_id


        AND op.operate_emp_id = #{operateEmpId}


        AND op.method_name LIKE CONCAT('%', #{methodName}, '%')


        AND op.operate_time >= #{beginTime}


        AND op.operate_time <= #{endTime}


ORDER BY op.operate_time DESC
